// Generate random email and name for unique registration
const randomId = Math.floor(Math.random() * 1000000);
pm.environment.set("$randomEmail", `test_user_${randomId}@example.com`);
pm.environment.set("$randomInt", randomId);
pm.environment.set("lastRegisteredEmail", pm.environment.get("$randomEmail"));
pm.environment.set("lastRegisteredPassword", "testpassword");

pm.test("Status code is 201 Created", function () {
    pm.response.to.have.status(201);
});

const responseJson = pm.response.json();
pm.test("Response indicates success", function () {
    pm.expect(responseJson.success).to.be.true;
});

pm.test("User ID and Token are present", function () {
    pm.expect(responseJson.data).to.have.property('user');
    pm.expect(responseJson.data.user).to.have.property('id');
    pm.expect(responseJson.data).to.have.property('token');
});

// Save token and user ID for subsequent requests
if (responseJson.success && responseJson.data && responseJson.data.token && responseJson.data.user && responseJson.data.user.id) {
    pm.environment.set("authToken", responseJson.data.token);
    pm.environment.set("userId", responseJson.data.user.id);
    console.log("authToken set:", pm.environment.get("authToken").substring(0, 20) + "...");
    console.log("userId set:", pm.environment.get("userId"));
}

